// [[Rcpp::export]]
std::vector<double> cumulativeProduct(std::vector<double> x)
{
    std::vector<double> out = x;

    for (size_t i = 1; i < x.size(); i++)
    {
        out[i] = out[i - 1] * x[i];
    }

    return out;
}
