template <typename T>
std::vector<T> uniqueC(const std::vector<T> &x)
{
    std::unordered_set<T> xSet(x.begin(), x.end());
    std::vector<T> xUnique;
    xUnique.insert(xUnique.end(), xSet.begin(), xSet.end());

    return xUnique;
}#include <iostream>
